import Button from "./components/UI/Button/Button";
import "./App.css";
import DemoOutput from "./components/Demo/Demo";

console.log("im outside app");
function App() {
  const [showP, setShowP] = useState(false);
  const [allowToggle, setAllowToggle] = useState(false);
  const [changeTitle, setChangeTitle] = useState(false);

  console.log("im in app");
  const handleShowP = useCallback(() => {
    if (allowToggle) {
      setShowP((prevShow) => !prevShow);
    }
  }, [allowToggle]);

  const changeTitleHandler = () => {};

  const handleToggle = () => {
    setAllowToggle(true);
  };
  return (
    <div className='app'>
      <DemoOutput show={showP} />
      <Button onClick={handleToggle}>Allow toggle</Button>
      <Button onClick={handleShowP}>Show Paragraph</Button>
    </div>
  );
}

export default App;
